import React, { Component } from 'react';

export default class CreateUsers extends Component {
  constructor(props) {
    super(props)
    this.state = {
      username: ''
    }
  }

  onChangeUsername = (e) => {
    this.setState({
      username: e.target.value
    })
  }

  onSubmit = (e) => {
    e.preventDefault()
    const user = {
      username: this.state.username,
    }
    console.log(user);
    this.setState({
      username: ''
    })
  }

  render() {
    return (
      <div>
        <p>You are on the Create Users component!</p>
      </div>
    )
  }
}
